import "./App.css";
import * as React from "react";
import "./App.css";
import Rating from "@mui/material/Rating";
import { ChangeEvent, useState, useEffect } from "react";

function App() {
  const [track, setTrack] = useState("");
  const [artist, setArtist] = useState("");
  const [correctData, setCorrectData] = useState({ message: "" });
  const [rating, setRating] = useState(0);
  const [songComponents, setSongComponents] = useState([]);
  const [data, setData] = useState({ message: "" });

  const fetchDataFromServer = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/data");
      const result = await response.json();
      setCorrectData(result);
      alert("here");
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const addSongComponent = () => {
    sendData();
    const timeoutId = setTimeout(fetchDataFromServer, 2000);
    setSongComponents([
      ...songComponents,
      [
        <div>
          <p>{correctData.message}</p>
          <Rating value={rating} readOnly />
        </div>,
      ],
    ]);
  };
  const trackChange = (e: ChangeEvent<HTMLInputElement>) => {
    setTrack(e.target.value);
  };
  const artistChange = (e: ChangeEvent<HTMLInputElement>) => {
    setArtist(e.target.value);
  };
  const sendData = async () => {
    const response = await fetch("http://localhost:5000/api/data", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(track + "/" + artist + "/" + rating),
    });
    const result = await response.json();
    setData(result);
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1>Song Recommendation Tool</h1>
      </header>
      <div className="Song Search">
        <p>Song Search</p>
        <input placeholder="Song Name" value={track} onChange={trackChange} />
        <input placeholder="Artist" value={artist} onChange={artistChange} />
        <Rating
          name="controlled"
          precision={0.5}
          value={rating}
          onChange={(event, newRating) => {
            setRating(newRating);
          }}
        />
        <button onClick={addSongComponent}>+ Add Song</button>
        <div>
          {songComponents.map((component, index) => (
            <div key={index}>{component}</div>
          ))}
        </div>
        <button>Remove Song</button>
        <button>Submit</button>
      </div>
    </div>
  );
}

export default App;
