import "./App.css";
import * as React from "react";
import "./App.css";
import Rating from "@mui/material/Rating";
import { ChangeEvent, useState } from "react";

function App() {
  const [track, setTrack] = useState("");
  const [artist, setArtist] = useState("");
  const [trackfield, setTrackField] = useState("");
  const trackChange = (e: ChangeEvent<HTMLInputElement>) => {
    setTrack(e.target.value);
  };
  const artistChange = (e: ChangeEvent<HTMLInputElement>) => {
    setArtist(e.target.value);
  };
  const trackfieldChange = (e: ChangeEvent<HTMLButtonElement>) => {
    setTrackField(track + " - " + artist);
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1>Song Recommendation Tool</h1>
      </header>
      <div className="Song Search">
        <p>Song Search</p>
        <input placeholder="Song Name" value={track} onChange={trackChange} />
        <input placeholder="Artist" value={artist} onChange={artistChange} />
        <button onClick={trackfieldChange}>+ Add Song</button>
        <p>{trackfield}</p>
        <Rating name="simple-controlled" precision={0.5} />
        <button>Remove Song</button>
        <button>Submit</button>
      </div>
    </div>
  );
}

export default App;
