import "./App.css";
import * as React from "react";
import "./App.css";
import Rating from "@mui/material/Rating";
import { ChangeEvent, useState } from "react";

function App() {
  const [track, setTrack] = useState("");
  const [artist, setArtist] = useState("");
  const [trackfield, setTrackField] = useState("");
  const [rating, setRating] = useState();
  const [ratingComponents, setRatingComponents] = useState([]);
  const addRatingComponent = () => {
    setRatingComponents([
      ...ratingComponents,
      <Rating value={rating} readOnly />,
    ]);
  };
  const trackChange = (e: ChangeEvent<HTMLInputElement>) => {
    setTrack(e.target.value);
  };
  const artistChange = (e: ChangeEvent<HTMLInputElement>) => {
    setArtist(e.target.value);
  };
  const trackfieldChange = (e: ChangeEvent<HTMLButtonElement>) => {
    setTrackField(track + " - " + artist);
  };
  const addSong = () => {
    trackfieldChange();
    addRatingComponent();
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1>Song Recommendation Tool</h1>
      </header>
      <div className="Song Search">
        <p>Song Search</p>
        <input placeholder="Song Name" value={track} onChange={trackChange} />
        <input placeholder="Artist" value={artist} onChange={artistChange} />
        <Rating
          name="controlled"
          precision={0.5}
          value={rating}
          onChange={(newRating) => {
            setRating(newRating);
          }}
        />
        <button onClick={addSong}>+ Add Song</button>
        <p>{trackfield}</p>
        <div>
          {ratingComponents.map((component, index) => (
            <div key={index}>{component}</div>
          ))}
        </div>
        <button>Remove Song</button>
        <button>Submit</button>
      </div>
    </div>
  );
}

export default App;
